def identify_generation(year_of_birth):
    """Returns the generation an author belongs to based on their year of birth."""
    if year_of_birth < 1945:
        return "Silent Generation"
    elif 1945 <= year_of_birth < 1965:
        return "Baby Boomers"
    elif 1965 <= year_of_birth < 1980:
        return "Generation X"
    else:
        return "Millennial"

