def get_sentimental_value(text: str) -> float:
    """Return the sentimental value of the input text."""
    # Initialize a dictionary to store the sentimental values of each word
    sentimental_values = {}

    # Loop through each word in the text
    for word in text.split():
        # If the word is not in the sentimental values dictionary, add it and set its value to 0
        if word not in sentimental_values:
            sentimental_values[word] = 0

        # Increment the sentimental value of the word by 1
        sentimental_values[word] += 1

    # Return the average sentimental value of the text
    return sum(sentimental_values.values()) / len(sentimental_values)

