def get_gender_ratio(text):
    """Return the gender ratio in a text."""
    male_count = 0
    female_count = 0
    for sentence in text:
        for word in sentence:
            if word == "he":
                male_count += 1
            elif word == "she":
                female_count += 1
    return female_count / (male_count + female_count)

def get_sexual_orientation_ratio(text):
    """Return the sexual orientation ratio in a text."""
    lesbian_count = 0
    gay_count = 0
    bi_count = 0
    for sentence in text:
        for word in sentence:
            if word == "she/her":
                lesbian_count += 1

