def parent_info(people):
    """Return a dictionary of parent information given a list of people."""
    parents = {}
    for person in people:
        if person["father"]:
            parents["Fathers"].append(person["father"])
        if person["mother"]:
            parents["Mothers"].append(person["mother"])
    return parents

