def get_family_info(people):
    """Return a dictionary containing information about the family of the given people."""
    family = {}
    for person in people:
        name = person["name"]
        family[name] = {
            "parents": [],
            "sibling": []
        }
        for sibling in person["siblings"]:
            family[name]["parents"].append(sibling["parent"])
            family[name]["sibling"].append(sibling)
    return family

