def get_parent_info(people):
    """
    Given a list of people, return a dictionary with the parents' information.

    The dictionary should have the format:
    {
        "parent1": {
            "name": "John Doe",
            "occupation": "Doctor"
        },
        "parent2": {
            "name": "Jane Doe",
            "occupation": "Teacher"
        }
    }
    """
    parents = {}
    for person in people:
        name = person["name"]
        occupation = person["occupation"]
        parents[name] = {
            "name": name,
            "occupation": occupation
        }
    return parents

